import React, { Component } from 'react';
import {Row, Col } from 'react-flexbox-grid';
import './index.css';
// import Markdown from 'react-markdown'; 
import marked from 'marked';
import hljs from 'highlight.js';

import Mnavbar from './Mnavbar';


export default class NoteDetail extends Component {

    constructor(props){
        super(props);
        this.content = React.createRef();
        this.state = {
            content:"",
        };
    }

    componentDidMount() {
        marked.setOptions({
            renderer: new marked.Renderer(),
            gfm: true,
            tables: true,
            breaks: true,
            pedantic: false,
            smartypants: false,
            highlight: function(code) {
                return hljs.highlightAuto(code).value;
            },
        });

        fetch(this.props.note.md)
          .then(res => res.text())
          .then(text => this.setState({content: text}));
    }

    componentDidUpdate(prevProps, prevState){
        if(prevProps.note.name !== this.props.note.name){
            fetch(this.props.note.md)
                .then(res => res.text())
                .then(text => this.setState({content: text}));
        }else{
            console.log("hhhh")
            console.log(this.content.current)
        }
    }

    // shouldComponentUpdate(nextProps, nextState){
    //     if(nextProps.note.name === this.props.note.name && nextState.md === this.state.md){
    //         return false;
    //     }
    //     return true;
    // }

	render(){
        const {title, time} = this.props.note;
        const content = <div
            id="note-content"
            ref={this.content}
            className="article-detail"
            dangerouslySetInnerHTML={{
                __html: this.state.content ? marked(this.state.content) : null,
                }}
            />;
		return (
			<div className="noteDetail">
                <Row start="xs" className="big-device">
                    <Col lg={3} className="article-navbar">
                        <Mnavbar source={this.state.content}></Mnavbar>
                    </Col>
                </Row>
                <Row>
                    <Col xsOffset={1} xs={10} lgOffset={4} lg={8} xlOffset={3} xl={9} 
                        className="article">
                        <h2 className="article-title">{title}</h2>
                        <p className="article-time">{time}</p>
                        <Row start="xs">
                            <Col xs={12}>{content}</Col>
                        </Row>
                    </Col>
                </Row>
                {/* <h2 className="article-title">{title}</h2>
                <p className="article-time">{time}</p>
                <Row  className="small-device">
                    <Col xsOffset={1} xs={10}>
                        {content}
                    </Col>
                </Row> */}
			</div>
		  );
	}
};
